function InitializeAnimations(){setTimeout(()=>{const e=document.querySelectorAll(".fluent-context-menu-container");for(const t of e)t.addEventListener("mouseenter",function(e){e.preventDefault(),e.target.querySelector(".fluent-context-menu").animate([{transform:"translateY(-20px)",opacity:"0",easing:"ease-out"},{transform:"translateY(0px)",opacity:"1",easing:"ease-in"}],{duration:90})})})}